const base_url="https://pepecoin-gannufan.herokuapp.com";localStorage.getItem("token")||(window.location.href="../../login.html"),fetch(base_url+"/api/v1/leaderboard",{method:"get",headers:{"Content-Type":"application/json"}}).then(e=>e.json()).then(t=>{if(console.log(t),"success"===t.status)for(let e=0; e<=t.data.users.length-1; e++){var s=`<li class="grid grid-cols-3 justify-items-stretch leaderboard mb-2 px-4">
            <p class="p--normal justify-self-start">${e+1}</p>
            <p class="p--normal justify-self-center">${t.data.users[e].ppname}</p>
            <p class="p--normal justify-self-end">${t.data.users[e].coins} PP</p>
            </li>`;document.querySelector("#leaderboard").insertAdjacentHTML("beforeend",s)}else{let e=document.querySelector(".error"),s=document.querySelector(".error p");s.innerHTML=t.message,e.classList.remove("hidden")}});