const base_url="https://pepecoin-gannufan.herokuapp.com";localStorage.getItem("token")||(window.location.href="../../login.html"),document.querySelector("#confirmBtn").addEventListener("click",()=>{var e=document.querySelector("#sendTo").value,o=document.querySelector("#amount").value,t=document.querySelector("#reason").value,n=document.querySelector("#message").value;fetch(base_url+"/api/v1/transfers",{method:"post",headers:{"Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")},body:JSON.stringify({recipient:e,reason:t,message:n,amount:o})}).then(e=>e.json()).then(t=>{if(console.log(t),"success"===t.status)window.location.href="index.html";else{let e=document.querySelector(".error"),o=document.querySelector(".error p");o.innerHTML=t.message,e.classList.remove("hidden")}})});