const base_url="https://pepecoin-gannufan.herokuapp.com";document.querySelector("#loginBtn").addEventListener("click",()=>{var e=document.querySelector("#email").value,t=document.querySelector("#password").value;fetch(base_url+"/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:e,password:t})}).then(e=>e.json()).then(o=>{if("success"===o.status){var e=o.data.token;localStorage.setItem("token",e);e=o.data.id;localStorage.setItem("id",e),window.location.href="index.html"}else{let e=document.querySelector(".error"),t=document.querySelector(".error p");t.innerHTML=o.message,e.classList.remove("hidden")}})});