const base_url="https://pepecoin-gannufan.herokuapp.com";document.querySelector("#signUpBtn").addEventListener("click",()=>{var e=document.querySelector("#email").value,t=document.querySelector("#username").value,n=document.querySelector("#firstname").value,r=document.querySelector("#lastname").value,o=document.querySelector("#password").value;fetch(base_url+"/users/signup",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:e,ppname:t,firstname:n,lastname:r,password:o})}).then(e=>e.json()).then(n=>{if("success"===n.status){var e=n.data.token;localStorage.setItem("token",e),window.location.href="login.html"}else{let e=document.querySelector(".error"),t=document.querySelector(".error p");t.innerHTML=n.message,e.classList.remove("hidden")}})});